import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './components/header';

const Home = () => {
    const [user, setUser] = useState(null);

    useEffect(() => {
        // Récupérer le token du local storage
        const token = localStorage.getItem('token');
        // Vérifier si le token existe
        if (token) {
            // Définir les entêtes de la requête avec le token JWT
            axios.defaults.headers.common['Authorization'] = token;

            // Effectuer une requête pour obtenir les informations de l'utilisateur
            axios.get('http://localhost:3001/users/user')
                .then(response => {
                    if(response.data.user == null){
                        localStorage.removeItem('token');
                        window.location.href = '/login';
                    }
                    setUser(response.data.user);
                })
                .catch(error => {
                    console.error('Erreur lors de la récupération des informations de l\'utilisateur :', error);
                    // Si aucun utilisateur n'est trouvé, supprimer le token et rediriger vers la page de login
                    localStorage.removeItem('token');
                    window.location.href = '/login';
                });
        }else{
            localStorage.removeItem('token');
            window.location.href = '/login';
        }
    }, []);

    return (
        <div>
            <Header />
        </div>
    );
};

export default Home;
