import React, { useState } from 'react';
import Header from '../components/header';

const CreateQuizz = () => {


    const [title, setTitle] = useState('');
    const [questions, setQuestions] = useState(['']);

    const handleAddQuestion = () => {
        setQuestions([...questions, '']);
    };

    const handleRemoveQuestion = (index) => {
        const newQuestions = [...questions];
        newQuestions.splice(index, 1);
        setQuestions(newQuestions);
    };

    const handleQuestionChange = (index, value) => {
        const newQuestions = [...questions];
        newQuestions[index] = value;
        setQuestions(newQuestions);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Logique pour envoyer le formulaire
    };

    return (
        <div>
            <h2>Ajouter un quizz</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="title" className="form-label">Titre</label>
                    <input type="text" className="form-control" id="title" value={title} onChange={(e) => setTitle(e.target.value)} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Questions</label>
                    {questions.map((question, index) => (
                        <div key={index} className="input-group mb-3">
                            <input type="text" className="form-control" value={question} onChange={(e) => handleQuestionChange(index, e.target.value)} required />
                            <button type="button" className="btn btn-outline-secondary" onClick={() => handleRemoveQuestion(index)}>Supprimer</button>
                        </div>
                    ))}
                    <button type="button" className="btn btn-primary" onClick={handleAddQuestion}>Ajouter une question</button>
                </div>
                <button type="submit" className="btn btn-primary">Cr√©er</button>
            </form>
        </div>
    );
};

export default CreateQuizz;
