import React, { useState, useEffect } from 'react';
import Header from '../components/header';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Quizz = () => {
    const [quizzes, setQuizzes] = useState([]);

    useEffect(() => {
        // Effectuer une requête pour obtenir la liste des quiz
        axios.get('http://localhost:3001/api/quizz')
            .then(response => {
                setQuizzes(response.data.quizzes);
            })
            .catch(error => {
                console.error('Erreur lors de la récupération des quiz :', error);
            });
    }, []);

    return (
        <div>
            <Header />
            <div className='my-4'>

            </div>
            <h2>Liste des quiz</h2>
            <div className="row">
                {quizzes.map(quiz => (
                    <div key={quiz.id} className="col-md-4 mb-4">
                        <div className="card">
                            <div className="card-body">
                                <h5 className="card-title">{quiz.titre}</h5>
                                <p className="card-text">Description du quiz</p>
                                <Link to={`/quizz/edit/${quiz.id}`} className="btn btn-primary">Modifier</Link>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Quizz;
