import React, { useState, useEffect }  from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Header = () => {
    const [user, setUser] = useState(null);
    useEffect(() => {
        // Récupérer le token du local storage
        const token = localStorage.getItem('token');
        // Vérifier si le token existe
        if (token) {
            // Définir les entêtes de la requête avec le token JWT
            axios.defaults.headers.common['Authorization'] = token;

            // Effectuer une requête pour obtenir les informations de l'utilisateur
            axios.get('http://localhost:3001/users/user')
                .then(response => {
                    if(response.data.user == null){
                        localStorage.removeItem('token');
                        window.location.href = '/login';
                    }
                    setUser(response.data.user);
                })
                .catch(error => {
                    console.error('Erreur lors de la récupération des informations de l\'utilisateur :', error);
                    // Si aucun utilisateur n'est trouvé, supprimer le token et rediriger vers la page de login
                    localStorage.removeItem('token');
                    window.location.href = '/login';
                });
        }else{
            localStorage.removeItem('token');
            window.location.href = '/login';
        }
    }, []);
    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container-fluid">
                <Link className="navbar-brand" to="/">Accueil</Link>
                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                        <li className="nav-item">
                            <div className="dropdown">
                                <button className="btn btn-outline-success dropdown-toggle" type="button" id="dropdownMenuButton" data-bs-toggle="dropdown" aria-expanded="false">
                                    Quizz
                                </button>
                                <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                                    <li><Link className="dropdown-item" to="/quizz/create">Ajouter un quizz</Link></li>
                                    <li><Link className="dropdown-item" to="/quizz">Voir mes quizz</Link></li>
                                </ul>
                            </div>
                        </li>
                        {/* Ajoutez d'autres liens ici */}
                    </ul>
                    {user ? (
                        <div>
                            <p className="mb-0">Bienvenue, {user.firstName} {user.lastName} !</p>
                            {/* Ajoutez un lien pour se déconnecter si nécessaire */}
                        </div>
                    ) : (
                        <Link className="btn btn-outline-success" to="/login">Se connecter</Link>
                    )}
                </div>
            </div>
        </nav>
    );
};

export default Header;
