        };

        console.log('Data to be sent:', data);

        axios.post('http://localhost:3001/api/quizz', data, {
            headers: {
                'Authorization': token // Ajouter le token dans les en-têtes
            }
        })
        .then(response => {
            // Traiter la réponse du backend si nécessaire
            console.log(response.data);
            // Rediriger ou afficher un message de succès
        })
        .catch(error => {
            console.error('Erreur lors de la création du quizz :', error);
            // Afficher un message d'erreur
        });
    };

    return (
        <div>
            <Header />
            <div className="container">
                <div className="row justify-content-center mt-5">
                    <div className="col-md-6">
                        <div className="card p-4">
                            <h2 className="mb-4">Création de groupe</h2>
                            <form onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <label htmlFor="name" className="form-label">Nom</label>
                                    <input type="text" className="form-control" id="name" value={name} onChange={(e) => setName(e.target.value)} required />
                                </div>
                                <button type="submit" className="btn btn-primary">Créer</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CreateQuizz;
